!function(e,t){e.component("vue-widget-tab",{props:{items:Array,activeIndex:{twoWay:!0,type:Number,"default":-1}},template:'<div data-active-index="{{activeIndex}}" class="vue-widget-tab"><div class="item" data-active="{{$index==activeIndex}}" v-for="item in items" @click="item_click($index)">{{item.text}}</div><b class="bar-slide" :style="{left:activeIndex * 33.33 +\'%\'}"></b></div>',methods:{item_click:function(e){this.items.map(function(e){e.active=!1}),this.items[e].active=!0,this.activeIndex=e}}})}(Vue,window);